// src/Chat.js
import React, { useState } from 'react';
import './Chat.css';

function Chat() {
  const [messages, setMessages] = useState([
    { text: 'I want to write a story about a blue stone...', sender: 'user' },
    {
      text: `Certainly! Here's a starting point for your story: Chapter 1: The Discovery
        In the heart of a dense jungle, where sunlight barely pierced the thick canopy overhead...`,
      sender: 'bot',
    },
  ]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');
    }
  };

  return (
    <div className="Chat">
      <div className="Chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`Chat-message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="Chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message here..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
