// src/Chat.js
import React, { useState, useEffect, useRef } from 'react';
import { Box, TextField, Button, Typography, Paper, styled } from '@mui/material';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';

const ChatContainer = styled(Paper)({
  width: '50%',
  backgroundColor: '#ffffff',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  boxSizing: 'border-box',
  height: '100%',
});

const ChatMessages = styled(Box)({
  flexGrow: 1,
  padding: '10px',
  overflowY: 'auto',
});

const ChatMessageContainer = styled(Box)({
  display: 'flex',
  alignItems: 'flex-start',
  margin: '10px 0',
});

const ChatMessage = styled(Box)(({ user }) => ({
  display: 'flex',
  alignItems: 'center',
  marginLeft: '8px',
  padding: '10px',
  borderRadius: '5px',
  backgroundColor: user ? '#f5f5f5' : '#ffe1e1',
  maxWidth: '80%',
}));

const ChatInput = styled(Box)({
  display: 'flex',
  padding: '10px',
  borderTop: '1px solid #ccc',
  backgroundColor: '#fff',
});

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, user: true }]);
      setInput('');
      // Simulate a typing effect for bot response
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: '...', user: false, typing: true },
        ]);
        setTimeout(() => {
          setMessages((prevMessages) => [
            ...prevMessages.slice(0, -1),
            { text: 'This is a bot response.', user: false },
          ]);
        }, 1000);
      }, 500);
    }
  };

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <ChatContainer elevation={3}>
      <ChatMessages>
        {messages.map((message, index) => (
          <ChatMessageContainer key={index}>
            {message.user ? <PersonIcon /> : <SmartToyIcon />}
            <ChatMessage user={message.user}>
              <Typography>{message.text}</Typography>
            </ChatMessage>
          </ChatMessageContainer>
        ))}
        <div ref={messagesEndRef} />
      </ChatMessages>
      <ChatInput>
        <TextField
          fullWidth
          variant="outlined"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <Button variant="contained" color="primary" onClick={handleSend}>
          Send
        </Button>
      </ChatInput>
    </ChatContainer>
  );
}

export default Chat;
