// src/Chat.js
import React, { useState } from 'react';
import { Box, TextField, Button, Typography, Paper, styled } from '@mui/material';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';

const ChatContainer = styled(Paper)({
  width: '50%',
  backgroundColor: '#ffffff',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-between',
  boxSizing: 'border-box',
  height: '100%',
});

const ChatMessages = styled(Box)({
  flexGrow: 1,
  padding: '10px',
  overflowY: 'auto',
});

const ChatMessage = styled(Box)(({ user }) => ({
  display: 'flex',
  alignItems: 'center',
  margin: '10px 0',
  padding: '10px',
  borderRadius: '5px',
  alignSelf: user ? 'flex-start' : 'flex-end',
  backgroundColor: user ? '#f5f5f5' : '#ffe1e1',
}));

const ChatInput = styled(Box)({
  display: 'flex',
  padding: '10px',
  borderTop: '1px solid #ccc',
  backgroundColor: '#fff',
});

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, user: true }]);
      setInput('');
      // Simulate a bot response
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: 'This is a bot response.', user: false },
        ]);
      }, 1000);
    }
  };

  return (
    <ChatContainer elevation={3}>
      <ChatMessages>
        {messages.map((message, index) => (
          <ChatMessage key={index} user={message.user}>
            {message.user ? <PersonIcon /> : <SmartToyIcon />}
            <Typography style={{ marginLeft: 8 }}>{message.text}</Typography>
          </ChatMessage>
        ))}
      </ChatMessages>
      <ChatInput>
        <TextField
          fullWidth
          variant="outlined"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <Button variant="contained" color="primary" onClick={handleSend}>
          Send
        </Button>
      </ChatInput>
    </ChatContainer>
  );
}

export default Chat;
